package com.framework.config;


import com.kratos.kits.alipay.annotation.builder.AliPayConfig;
import com.kratos.kits.alipay.annotation.configuration.AliPayAPIConfigurer;
import org.springframework.context.annotation.Configuration;

/**
 * 支付宝配置
 */
@Configuration
public class AliPayAPIConfig implements AliPayAPIConfigurer {

    @Override
    public void configure(AliPayConfig aliPayConfig) {
        aliPayConfig.setAppId("2017120700440152");
        aliPayConfig.setPrivateKey("");
        aliPayConfig.setAppPublicKey("");
        aliPayConfig.setAlipayPublicKey("");
        aliPayConfig.setNotifyUrl("http://jifen.dldjshop.com/ali/pay/notify");
        aliPayConfig.setReturnUrl("https://jifen.dldjshop.com/wechat/orderform/list");
    }
}
